from Bio import pairwise2
from Bio.pairwise2 import format_alignment


from Bio.Align import substitution_matrices
blosum62 = substitution_matrices.load("BLOSUM62")


# Define two sequences to be aligned
X = "MAELSEQVQNLSINDNNENGYVPPHLRGKPRSARNNSSNYNNNNGGYNGGRGGGSFFSNNRRGGYGNGGFFGGNNGGSRSNGRSGGRW"
Y = 'MADLQEQVQNLNINDDDKNASSYVPPHLRNQRRGPPRHDSPSDGSENNNDFGFGFNNRRGGYNGGSGYRGGNNSYNRRGNYQGGQNNSNNNNNGGGFNRIPGRGSW'

alignments = pairwise2.align.globaldx(X, Y, blosum62)
print(max(alignments))

# Use format_alignment method to format the alignments in the list
for a in alignments:
    print(format_alignment(*a))


